import React, { Component } from 'react';
import './App.css';
import FrontScreen from './FrontScreen';
import RecordFuel from './RecordFuel';
import RecordMileage from './RecordMileage';
import ResultsScreen from './ResultsScreen';
import Button from './Button';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "ScreenSwapper",
      showFrontScreen: true,
      showRecordFuel: false,
      showRecordMileage: false,
      showResultsScreen: false
    };

    this.hideComponent = this.hideComponent.bind(this);

  }

  hideComponent(name) {

    console.log(name);

    switch (name) {
      case "showRecordFuel":
        this.setState({ showRecordFuel: !this.state.showRecordFuel });
        break;

      case "showRecordMileage":
        this.setState({ showRecordMileage: !this.state.showRecordMileage });
        break;

      case "showResultsScreen":
        this.setState({ showResultsScreen: !this.state.showResultsScreen });
        break;

      default:
        this.setState({ showFrontScreen: !this.state.showFrontScreen });

    }
  }

  render() {

    const { showFrontScreen, showRecordFuel, showRecordMileage, showResultsScreen } = this.state;
    return (
      <div className="container">

      {/* Title */}
      <div class="jumbotron">
          <h3 class="display-5">Fuel + Mileage Tracker</h3>
      </div>

      {/* RecordFuel Button */}
      <div class="card">
          <Button theme="RecordButton" onClick={() => this.hideComponent("showRecordFuel")}>
              Record Fuel
          </Button>
      </div>

      {/* RecordMileage Button */}
      <div class="card">
          <Button theme="RecordButton" onClick={this.recordMileageToggle}>
              Record Mileage
          </Button>
      </div>

      {/* ShowResults Button */}
      <div class="card">
          <Button theme="ResultsButton" onClick={this.resultsScreenToggle}>
              View Results
          </Button>


          {showRecordFuel && <RecordFuel />}

          {showRecordMileage && <RecordMileage />}

          {showResultsScreen && <ResultsScreen />}

        </div>
      </div>

    );
  }
}
export default App;
