import React, { Component } from 'react';
import styled from 'styled-components';
import './App.css';
import MyChart from './MyChart';
import RecordFuel from './RecordFuel';
import ButtonsContainer from './FrontScreen';



class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showRecordFuel: false,
      showButtonsContainer: true
    }

    this.showRecordFuel = this.showRecordFuel.bind(this);


  }

  showRecordFuel() {
    this.setState(
      {
        showRecordFuel: true,
        showButtonsContainer: false
      })
  }



  render() {
    return (

        <div class="col-13 offset-0 my-5">
              {
                this.state.showRecordFuel ? <RecordFuel /> : null
              }

              {
                this.state.showButtonsContainer ? <ButtonsContainer showRecordFuel={this.showRecordFuel} /> : null
              }
        </div>
    );
  }
}
export default App;










