import React, { Component } from 'react';
import Button from './Button';

class RecordFuel extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showRecordFuel: true,
      showFrontScreen: false,
      showRecordMileage: false,
      showResultsScreen: false,

      // selectedFile: null
    }

    this.frontScreenToggle = this.frontScreenToggle.bind(this);
  }

  frontScreenToggle() {
    this.props.frontScreenToggle()
  }

  frontScreenToggle() {
    this.setState(
      {
        showRecordFuel: false,
        showFrontScreen: true,
        showRecordMileage: false,
        showResultsScreen: false
      });
  } 

  render() {
    return (

      <div class="card">
        <div class="card-body">

          {/* Back Button */}
          <Button theme="BackButton" onClick={this.frontScreenToggle}>
            Back
          </Button>

          {/* Title */}
          <div class="jumbotron">
            <h3 class="display-5"> Fuel Expenditure, Record Fuel</h3>
          </div>
        </div>

        {/* Fuel Type Drop Down (If Petrol/Diesel Switch input to (L) if electric (KWH)*/}
        <div>
          <label>
            Fuel Type:
            <select>
              <option value="petrol">Petrol</option>
              <option value="diesel">Diesel</option>
              <option value="electric">Electric</option>
              <option value="hybrid">Hybrid</option>
            </select>
          </label>
        </div>

        {/* Amount Input*/}
        <form>
          <label>
            Amount (L):
            <input type="number" name="amount" placeholder="Enter Amount" />
          </label>
          <br />

          {/* Price Input */}
          <label>
            Price (Â£):
            <input type="number" name="price" placeholder="Enter Price" />
            VAT inc?

            {/* VAT Checkbox */}
            <input type="checkbox" name="checkbox" />
          </label>
        </form>

        Time and Date Picker
        <br />
        <input type="file" onChange={this.fileSelectedHandler}placeholder = "Upload Picture" />
        {/* <button onClick={this.fileSelectedHandler}>Upload</button> */}
      </div>
    );
  }
}
export default RecordFuel;
