import React, { Component } from 'react';
import styled from 'styled-components';
import './App.css';
import MyChart from './MyChart';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showMe:true
    }
  }


  operation()
  {
    this.setState(
    {
      showMe:!this.setState.showMe  
    })
  }

  render() {

    //Individual Button Stylings
const theme = {
  RecordButton: {
      default: '#31CACF', //Light-Blue
      hover: '#D5D5D5'    //Light-Grey
  },
  ResultsButton: {
      default: '#F26627', //Orange
      hover: '#EF3D59'    //Red
  }
}

//Button Styling
const Button = styled.button`
  background-color: ${props => theme[props.theme].default};
  color: black;
  padding: 5px 15px
  border-radius: 5px;
  outline: 0;
  text-transform: uppercase;
  margin: 10px 0px;
  box-shadow: 0px 2px 2px lightgrey;
  cursor: pointer;
  transition: ease background-color 250ms;
  &:hover {
  background-color: ${props => theme[props.theme].hover};;
          }`

  function Main() {

  return (
  <>
  <div>
  <Button theme="RecordButton" onClick={this.operation}>
  Record Fuel
  </Button>
  </div>

  <div>
  <Button theme="RecordButton" onClick={()=>this.operation()}>
  Record Mileage
  </Button>
  </div>

  <div>
  <Button theme="ResultsButton" onClick={()=>this.operation()}>
  View Results
  </Button>
  </div>
  </>
  )
  }


  return (

    <div className="container">
        <div class="jumbotron">
          <h3 class="display-5">Fuel + Mileage Tracker</h3>
        </div>

              <div class="col-13 offset-0 my-5">
                  <div class="card">
                    <div class="card-body">
                      <h5>Fuel Expenditure</h5>
                      {
                        this.state.showMe?
                        <MyChart />
                        :null
                      }

                  <Main />
              </div>
            </div>
          </div>
        </div>
    );    
  }
}
export default App;
