const theme = {
    RecordButton: {
        default: '#31CACF', //Light-Blue
        hover: '#D5D5D5'    //Light-Grey
    },
    ResultsButton: {
        default: '#F26627', //Orange
        hover: '#EF3D59'    //Red
    }
}

//Button Styling
const Button = styled.button`
 background-color: ${props => theme[props.theme].default};
 color: black;
 padding: 5px 15px
 border-radius: 5px;
 outline: 0;
 text-transform: uppercase;
 margin: 10px 0px;
 box-shadow: 0px 2px 2px lightgrey;
 cursor: pointer;
 transition: ease background-color 250ms;
 &:hover {
     background-color: ${props => theme[props.theme].hover};;
 }
`

function Main() {

  return (
    <>
        <div>
            <Button theme="RecordButton" onClick={()=>operation()}>
            Record Fuel
            </Button>
        </div>

        <div>
        <Button theme="RecordButton" onClick={()=>operation()}>
            Record Mileage
            </Button>
        </div>

        <div>
        <Button theme="ResultsButton" onClick={()=>operation()}>
            View Results
            </Button>
        </div>
    </>
  )
}

export default Main;
