import React, { Component } from 'react';
import './App.css';
import MyChart from './MyChart';
import Main from './Main';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showMe:true
    }
  }

  operation()
  {
    this.setState()
    {
      showMe:false  
    }
  }

  render() {

    function Main() {

  return (
    <>
        <div>
            <Button theme="RecordButton" onClick={()=>this.operation()}>
            Record Fuel
            </Button>
        </div>
        <div>
        <Button theme="RecordButton" onClick={()=>this.operation()}>
            Record Mileage

            </Button>
        </div>
        <div>
        <Button theme="ResultsButton" onClick={()=>this.operation()}>
            View Results
            </Button>
        </div>
    </>
  )
}

  return (

    <div className="container">
        <div class="jumbotron">
          <h3 class="display-5">Fuel + Mileage Tracker</h3>
        </div>

              <div class="col-13 offset-0 my-5">
                  <div class="card">
                    <div class="card-body">
                      <h5>Fuel Expenditure</h5>
                      {
                        this.state.showMe?
                        <MyChart />
                        :null
                      }

                  <Main />
              </div>
            </div>
          </div>
        </div>
    );    
  }
}
export default App;
