import React, { Component } from 'react';
import Button from './Button';

class FrontScreen extends Component {
    constructor(props) {
        super(props);
        this.state = {
            showRecordFuel: false,
            showFrontScreen: true,
            showRecordMileage: false,
            showResultsScreen: false
        }
        this.recordFuelToggle = this.recordFuelToggle.bind(this);
        this.frontScreenToggle = this.frontScreenToggle.bind(this);
    }

    recordFuelToggle() {
        this.props.recordFuelToggle()
      }

    recordFuelToggle() {
        this.setState(
          {
            showRecordFuel: true,
            showFrontScreen: false,
            showRecordMileage: false,
            showResultsScreen: false
          });
      }

      frontScreenToggle() {
        this.props.frontScreenToggle()
      }

      frontScreenToggle() {
        this.setState(
          {
            showRecordFuel: true,
            showFrontScreen: false,
            showRecordMileage: false,
            showResultsScreen: false
          });
      }

    render() {
        return (
            <div className="container">

                {/* Title */}
                <div class="jumbotron">
                    <h3 class="display-5">Fuel + Mileage Tracker</h3>
                </div>

                {/* RecordFuel Button */}
                <div class="card">
                    <Button theme="RecordButton" onClick={this.recordFuelToggle}>
                        Record Fuel
                    </Button>
                </div>

                {/* RecordMileage Button */}
                <div class="card">
                    <Button theme="RecordButton" onClick={this.recordMileageToggle}>
                        Record Mileage
                    </Button>
                </div>

                {/* ShowResults Button */}
                <div class="card">
                    <Button theme="ResultsButton" onClick={this.resultsScreenToggle}>
                        View Results
                    </Button>
                </div>
            </div>
        );
    }
}
export default FrontScreen;
