import React, { Component } from 'react';
import styled from 'styled-components';
import './App.css';
import MyChart from './MyChart';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showMe:true
    }

    this.operation = this.operation.bind(this);

  }


  operation()
  {
    this.setState(
    {
      showMe:!this.setState.showMe  
    })
  }

  render() {
    return (

    <div className="container">
        <div class="jumbotron">
          <h3 class="display-5">Fuel + Mileage Tracker</h3>
        </div>

              <div class="col-13 offset-0 my-5">
                  <div class="card">
                    <div class="card-body">
                      <h5>Fuel Expenditure</h5>
                      {
                        this.state.showMe ? <MyChart /> : null
                      }





                      //Individual Button Stylings


                      <div>
                        <Button theme="RecordButton" onClick={this.operation}>
                        Record Fuel
                        </Button>
                      </div>

                      <div>
                        <Button theme="RecordButton" onClick={this.operation}>
                        Record Mileage
                        </Button>
                      </div>

                      <div>
                        <Button theme="ResultsButton" onClick={this.operation}>
                        View Results
                        </Button>
                      </div>




              </div>
            </div>
          </div>
        </div>
    );    
  }
}
export default App;










