import React, { Component } from 'react';
import styled from 'styled-components';
import './App.css';
import MyChart from './MyChart';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showMe:true
    }

    this.openRecordFuel = this.openRecordFuel.bind(this);

  }


  openRecordFuel()
  {
    this.setState(
    {
      showMe:!this.state.showMe  
    })
  }

