        </div>

              <div class="col-13 offset-0 my-5">
                  <div class="card">
                    <div class="card-body">

                    <h5>Fuel Expenditure</h5>
                      {
                        this.state.showMe ? <MyChart /> : null
                      }





                      //Individual Button Stylings


                      <div>
                        <button theme="RecordButton" onClick={this.openRecordFuel}>
                        Record Fuel
                        </button>
                      </div>

                      <div>
                        <button theme="RecordButton" onClick={this.operation}>
                        Record Mileage
                        </button>
                      </div>

                      <div>
                        <button theme="ResultsButton" onClick={this.operation}>
                        View Results
                        </button>
                      </div>




              </div>
            </div>
          </div>
        </div>
    );    
  }
}
export default App;










