import React, { Component } from 'react';
import Button from './Button';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';
import DateTimePicker from 'react-datetime-picker';

class RecordFuel extends Component {
  constructor(props) {
    super(props);
    this.state = {
    }

    this.showFrontScreen = this.showFrontScreen.bind(this);

  const [value, onChange] = useState(new Date());

  }

  showFrontScreen() {
    this.props.showFrontScreen()
  }


  render() {
    return (

      <div class="card">
        <div class="card-body">

          <Button theme="BackButton" onClick={this.showFrontScreen}>
            Back
          </Button>

          <div class="jumbotron">
            <h3 class="display-5"> Fuel Expenditure, Record Fuel</h3>
          </div>
        </div>


        <Dropdown>
          <Dropdown.Toggle variant="success" id="dropdown-basic">
            Fuel Type
          </Dropdown.Toggle>

          <Dropdown.Menu>
            <Dropdown.Item href="#/action-1">Petrol</Dropdown.Item>
            <Dropdown.Item href="#/action-2">Diesel</Dropdown.Item>
            <Dropdown.Item href="#/action-3">Electric</Dropdown.Item>
            <Dropdown.Item href="#/action-4">Hybrid</Dropdown.Item>
          </Dropdown.Menu>
        </Dropdown>

        <form>
          <label>
            Name:
            <input type="text" name="name" />
          </label>
          <input type="submit" value="Submit" />
        </form>

        <form>
          <label>
            Name:
            <input type="text" name="name" />
          </label>
          <input type="submit" value="Submit" />
        </form>

        <form>
          <label>
            Name:
            <input type="text" name="name" />
          </label>
          <input type="submit" value="Submit" />
        </form>

        <div>
          <DateTimePicker onChange={onChange} value={value} />
        </div>

      </div>
    );
  }
}
export default RecordFuel;
